	function uninstall(){
		$res = parent::uninstall();
		
		if( $res ){
			$database = _obj('Database');
			$database->execute('DROP TABLE danea_product');
			Form::delete('danea_setting');
			$dati = Marion::getConfig('danea_setting');
			foreach($dati as $k => $v){
				Marion::delConfig('danea_setting',$k);
			}

			
		}


		return $res;
	}

}



